import{_ as a,o as e,c as t,O as s}from"./chunks/framework.4afe7240.js";const S=JSON.parse('{"title":"CPSR寄存器","description":"","frontmatter":{"tags":"待成文","aliases":["CPSR","Current Program Status Register","程序状态寄存器"],"title":"CPSR寄存器","sidebar":false,"date":"2023-06-06T00:00:00.000Z"},"headers":[],"relativePath":"pages/CPSR寄存器.md","filePath":"pages/CPSR寄存器.md","lastUpdated":1686285451000}'),r={name:"pages/CPSR寄存器.md"},o=s('<h1 id="cpsr寄存器" tabindex="-1">CPSR寄存器 <a class="header-anchor" href="#cpsr寄存器" aria-label="Permalink to &quot;CPSR寄存器&quot;">​</a></h1><h2 id="作用" tabindex="-1">作用 <a class="header-anchor" href="#作用" aria-label="Permalink to &quot;作用&quot;">​</a></h2><p>记录当前程序的执行状态和控制处理器的行为</p><p>通过修改 CPSR 寄存器中的标志位，可以控制处理器的运行状态和行为，从而实现不同的功能。例如，可以通过修改中断使能标志位来开启或关闭中断，通过修改处理器模式标志位来切换不同的处理器模式等。</p><h2 id="定义" tabindex="-1">定义 <a class="header-anchor" href="#定义" aria-label="Permalink to &quot;定义&quot;">​</a></h2><p>属于ARM中的特殊寄存器，CPSR 寄存器的位数为 32 位，其中包含了很多标志位，用于记录当前处理器的运行状态，包括：</p><ol><li><p>条件码标志位（Condition Flags）：用于记录最近一次算术或逻辑运算的结果，包括零标志位（Z）、负数标志位（N）、进位标志位（C）、溢出标志位（V）等。</p></li><li><p>控制标志位（Control Flags）：用于控制处理器的行为，包括中断使能标志位（I）、快速中断使能标志位（F）、处理器模式标志位（M）等。</p></li><li><p>状态标志位（Status Flags）：用于记录处理器的状态，包括用户模式标志位（U）、不可屏蔽中断标志位（A）、粘滞位标志位（V）等。</p></li></ol>',7),i=[o];function l(n,_,p,c,d,h){return e(),t("div",null,i)}const C=a(r,[["render",l]]);export{S as __pageData,C as default};
