import{_ as a,o as t,c as s,z as e,a as o}from"./chunks/framework.4afe7240.js";const G=JSON.parse('{"title":"GPIO输出模式","description":"","frontmatter":{"tags":"待成文","title":"GPIO输出模式","sidebar":false,"date":"2023-06-06T00:00:00.000Z"},"headers":[],"relativePath":"pages/GPIO输出模式.md","filePath":"pages/GPIO输出模式.md","lastUpdated":1686281074000}'),r={name:"pages/GPIO输出模式.md"},n=e("h1",{id:"gpio输出模式",tabindex:"-1"},[o("GPIO输出模式 "),e("a",{class:"header-anchor",href:"#gpio输出模式","aria-label":'Permalink to "GPIO输出模式"'},"​")],-1),c=e("p",null,"GPIO 的输出模式有两种：推挽输出和开漏输出。其中，推挽输出是指输出引脚既可以输出高电平，也可以输出低电平，输出时具有较小的输出阻抗，可以直接驱动负载，是最常用的输出模式；而开漏输出是指输出引脚只能输出低电平，输出时具有较大的输出阻抗，需要通过外部上拉电阻将输出引脚连接到正电源，才能输出高电平信号。",-1),d=e("p",null,"在 STM32MP157 中，每个 GPIO 引脚都可以配置为推挽输出模式或开漏输出模式。具体的配置方法取决于所使用的开发板和编程工具，需要参考开发板的文档和 STM32MP157 的参考手册来进行配置。",-1),_=[n,c,d];function i(l,p,P,h,O,f){return t(),s("div",null,_)}const I=a(r,[["render",i]]);export{G as __pageData,I as default};
