import{_ as a,o as e,c as t,O as i}from"./chunks/framework.4afe7240.js";const C=JSON.parse('{"title":"IIC总线","description":"","frontmatter":{"tags":"待成文","title":"IIC总线","date":"2023-06-07T00:00:00.000Z"},"headers":[],"relativePath":"pages/IIC总线.md","filePath":"pages/IIC总线.md","lastUpdated":1686223285000}'),l={name:"pages/IIC总线.md"},r=i('<h1 id="iic总线" tabindex="-1">IIC总线 <a class="header-anchor" href="#iic总线" aria-label="Permalink to &quot;IIC总线&quot;">​</a></h1><h2 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to &quot;特点&quot;">​</a></h2><ul><li><a href="./同步总线.html">同步</a>、<a href="./串行总线.html">串行</a>、<a href="./全双工总线.html">全双工</a></li><li>信号线数 <ul><li>SDA</li><li>SCLK</li></ul></li></ul><h2 id="说明" tabindex="-1">说明 <a class="header-anchor" href="#说明" aria-label="Permalink to &quot;说明&quot;">​</a></h2><ul><li><a href="./UART工作原理.html">UART工作原理</a></li></ul><p>I2C（Inter-Integrated Circuit）是一种通信协议，也被称为IIC或TWI（Two-Wire Interface）。I2C协议是一种双线制串行通信协议，可以实现多个设备在同一总线上进行通信。I2C协议规定了数据的格式、传输速率、地址分配、起始和停止信号等内容，而I2C总线则提供了物理层的接口和信号传输的方式。</p><p>在I2C总线上，多个设备可以共享同一根双线制总线，其中一条线为数据线（SDA），另一条线为时钟线（SCL）。每个设备都有一个唯一的地址，可以发送和接收数据。I2C协议具有简单、灵活、可靠等优点，因此被广泛应用于各种电子设备中，如温度传感器、加速度计、电子罗盘等。</p><p>I2C是一种双线制串行通信协议，可以实现多个设备在同一总线上进行通信，常用于连接各种外设，如温度传感器、加速度计、电子罗盘等。</p>',8),o=[r];function s(I,n,c,h,_,d){return e(),t("div",null,o)}const u=a(l,[["render",s]]);export{C as __pageData,u as default};
