import{_ as a,o as e,c as t,O as s}from"./chunks/framework.4afe7240.js";const B=JSON.parse('{"title":"USB总线","description":"","frontmatter":{"tags":"待成文","aliases":["Universal Serial Bus","通用串行总线"],"title":"USB总线","date":"2023-06-07T00:00:00.000Z"},"headers":[],"relativePath":"pages/USB总线.md","filePath":"pages/USB总线.md","lastUpdated":1686218490000}'),r={name:"pages/USB总线.md"},o=s('<h1 id="usb总线" tabindex="-1">USB总线 <a class="header-anchor" href="#usb总线" aria-label="Permalink to &quot;USB总线&quot;">​</a></h1><p>USB（Universal Serial Bus，通用串行总线）是一种全双工的通信协议。全双工通信是指通信双方可以同时发送和接收数据，而不必等待对方的响应。在USB通信中，数据可以同时在两个方向上传输，即从主机到设备和从设备到主机。</p><p>USB使用四根线缆进行通信，其中两根线缆（D+和D-）用于数据传输，另外两根线缆（Vcc和GND）用于电源供应。在USB通信中，主机和设备通过交替地发送和接收数据包来进行通信，每个数据包包含一个同步标记、一个地址标记、一个端点标记、一个长度标记和一个CRC校验码。</p><h2 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to &quot;特点&quot;">​</a></h2><p><a href="./串行总线.html">串行</a>、<a href="./全双工总线.html">全双工</a></p>',5),_=[o];function i(l,n,c,d,p,h){return e(),t("div",null,_)}const U=a(r,[["render",i]]);export{B as __pageData,U as default};
