import{_ as t,o as a,c as s,z as e,a as o}from"./chunks/framework.4afe7240.js";const g=JSON.parse('{"title":"PC寄存器","description":"","frontmatter":{"aliases":["PC","Program Counter"],"tags":"待成文","title":"PC寄存器","date":"2023-06-06T00:00:00.000Z"},"headers":[],"relativePath":"pages/pc寄存器.md","filePath":"pages/pc寄存器.md","lastUpdated":1686223285000}'),r={name:"pages/pc寄存器.md"},c=e("h1",{id:"pc寄存器",tabindex:"-1"},[o("PC寄存器 "),e("a",{class:"header-anchor",href:"#pc寄存器","aria-label":'Permalink to "PC寄存器"'},"​")],-1),n=e("p",null,"PC（Program Counter）寄存器是 ARM 处理器中的一个特殊寄存器，用于存储当前正在执行的指令的地址。在 ARM 处理器中，PC 寄存器的位数为 32 位，可以存储 4GB 的地址空间。",-1),p=e("p",null,"当 ARM 处理器执行指令时，会根据 PC 寄存器中存储的地址，从内存中读取指令并执行。执行完当前指令后，处理器会自动将 PC 寄存器中的值增加 4，以便读取下一条指令。由于 ARM 处理器采用流水线执行指令，因此 PC 寄存器中的值并不是实时更新的，而是在当前指令执行完毕后才会更新。",-1),_=e("p",null,"在 ARM 处理器中，PC 寄存器的值可以通过多种方式修改。例如，可以通过跳转指令（如 B、BL、BX 等）将 PC 寄存器的值修改为指定的地址，从而实现跳转到指定的子程序或者函数。此外，当处理器发生中断或者异常时，也会通过修改 PC 寄存器的值来跳转到相应的中断处理程序或异常处理程序。",-1),d=[c,n,p,_];function l(i,P,C,h,m,u){return a(),s("div",null,d)}const B=t(r,[["render",l]]);export{g as __pageData,B as default};
