import{_ as a,o as e,c as t,O as s}from"./chunks/framework.4afe7240.js";const o="/assets/20230607155551273.4d1acf72.png",f=JSON.parse('{"title":"串行总线","description":"","frontmatter":{"tags":"概念","aliases":"串行","title":"串行总线","date":"2023-06-07T00:00:00.000Z"},"headers":[],"relativePath":"pages/串行总线.md","filePath":"pages/串行总线.md","lastUpdated":1686248495000}'),r={name:"pages/串行总线.md"},i=s('<h1 id="串行总线" tabindex="-1">串行总线 <a class="header-anchor" href="#串行总线" aria-label="Permalink to &quot;串行总线&quot;">​</a></h1><p>#待学习</p><h2 id="定义" tabindex="-1">定义 <a class="header-anchor" href="#定义" aria-label="Permalink to &quot;定义&quot;">​</a></h2><ul><li>数字系统中数据传输方式</li><li>传输数据时，每次只能传输一位二进制数</li></ul><h2 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to &quot;特点&quot;">​</a></h2><ul><li>一位一位地传输，传输速率比较慢，但是可以使用较少的线路来传输数据。串行总线的优点是线路数量少、传输距离远、抗干扰能力强，常用于长距离传输和高速通信。</li></ul><p>而并行总线是指在传输数据时，同时传输多位二进制数，也就是同时传输多位二进制数据，传输速率比较快，但是需要使用较多的线路来传输数据。并行总线的优点是传输速率快、传输数据量大、传输效率高，常用于短距离传输和高速数据传输。</p><p>总的来说，串行总线和并行总线各有优缺点，具体应用需要根据实际情况来选择。在数字系统中，常见的串行总线有UART、SPI、I2C等，常见的并行总线有PCI、ISA、VME等。</p><p><img src="'+o+'" alt=""></p>',9),l=[i];function _(n,c,d,p,h,u){return e(),t("div",null,l)}const P=a(r,[["render",_]]);export{f as __pageData,P as default};
