import{_ as a,o as t,c as e,V as s}from"./chunks/framework.5a3bb230.js";const o="/assets/20230625204702397.1ae5934f.png",n="/assets/20230625204719902.ecc809e3.png",l="/assets/20230625204736371.51fe9aad.png",g=JSON.parse('{"title":"搭建 NFS 服务器","description":"","frontmatter":{"tags":"笔记","date":"2023-06-17T00:00:00.000Z","title":"搭建 NFS 服务器","sidebar":false},"headers":[],"relativePath":"pages/搭建 NFS 服务器.md","filePath":"pages/搭建 NFS 服务器.md","lastUpdated":1687705992000}'),r={name:"pages/搭建 NFS 服务器.md"},c=s('<h1 id="搭建-nfs-服务器" tabindex="-1">搭建 NFS 服务器 <a class="header-anchor" href="#搭建-nfs-服务器" aria-label="Permalink to &quot;搭建 NFS 服务器&quot;">​</a></h1><h2 id="安装-nfs-服务器" tabindex="-1">安装 NFS 服务器 <a class="header-anchor" href="#安装-nfs-服务器" aria-label="Permalink to &quot;安装 NFS 服务器&quot;">​</a></h2><h3 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h3><p><img src="'+o+'" alt=""></p><h3 id="配置" tabindex="-1">配置 <a class="header-anchor" href="#配置" aria-label="Permalink to &quot;配置&quot;">​</a></h3><p><img src="'+n+'" alt=""></p><h3 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h3><p>挂载的 mnt</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">sudo mount -t nfs 192.168.0.180:/home/shelton/Workspaces/nfs/rootfs /mnt</span></span></code></pre></div><blockquote><p>必须在ubuntu中操作，不能用sshl的去连接</p></blockquote><p>取消挂载</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">sudo umount /mnt</span></span></code></pre></div><blockquote><p>有进程使用nfs服务器时，无法取消挂载</p></blockquote><h2 id="作用" tabindex="-1">作用 <a class="header-anchor" href="#作用" aria-label="Permalink to &quot;作用&quot;">​</a></h2><p><img src="'+l+'" alt=""></p><p>1、在~/tftpboot目录下，准备需要下载开发板中的文件 2、在EMMC中/TF卡中烧写uboot镜像文件 3、开饭需要上电，uboot镜像文件被加载到内存中运行 4、uboot进行启动，启动成功之后，会在串口工具打印uboot信息 5、使用tftp命令下载程序到开发板内存中，内核进行启动 6、内核启动成功之后，挂载根文件系统</p>',16),p=[c];function i(d,h,_,u,m,b){return t(),e("div",null,p)}const q=a(r,[["render",i]]);export{g as __pageData,q as default};
