import{_ as s,o as a,c as l,V as n}from"./chunks/framework.5a3bb230.js";const t="/assets/20230625204427084.115af40f.png",p="/assets/20230625204446372.9cd6bdcc.png",F=JSON.parse('{"title":"搭建 TFTP 服务器","description":"","frontmatter":{"tags":"笔记","date":"2023-06-25T00:00:00.000Z","title":"搭建 TFTP 服务器","sidebar":false},"headers":[],"relativePath":"pages/搭建 TFTP 服务器.md","filePath":"pages/搭建 TFTP 服务器.md","lastUpdated":1687706030000}'),o={name:"pages/搭建 TFTP 服务器.md"},e=n(`<h1 id="搭建-tftp-服务器" tabindex="-1">搭建 TFTP 服务器 <a class="header-anchor" href="#搭建-tftp-服务器" aria-label="Permalink to &quot;搭建 TFTP 服务器&quot;">​</a></h1><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><blockquote><p>TFTP：基于UDP协议，用于传输文本文件（基于网络上传下载）</p></blockquote><ul><li>TFTP 服务器 <ul><li>tftpd-hpa <ul><li>功能简单，配置简单</li></ul></li><li>tftp-hpa（<strong>这个也可以安装</strong>） <ul><li>支持更多功能，配置更灵活</li></ul></li></ul></li></ul><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">apt-get</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">tftpd-hpa</span></span></code></pre></div><h2 id="配置" tabindex="-1">配置 <a class="header-anchor" href="#配置" aria-label="Permalink to &quot;配置&quot;">​</a></h2><p>编辑配置文件 <code>tftpd-hpa</code></p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">vim</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/etc/default/tftpd-hpa</span></span></code></pre></div><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># /etc/default/tftpd-hpa</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;">#指定 TFTP 服务器使用的用户</span></span>
<span class="line"><span style="color:#A6ACCD;">TFTP_USERNAME</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">tftp</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;">#指定 TFTP 服务器的根目录</span></span>
<span class="line"><span style="color:#A6ACCD;">TFTP_DIRECTORY</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">/home/shelton/Workspaces/tftpboot</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 指定 TFTP 服务器监听的地址和端口号</span></span>
<span class="line"><span style="color:#A6ACCD;">TFTP_ADDRESS</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">0.0.0.0:69</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 指定 TFTP 服务器的一些选项</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#676E95;font-style:italic;"># &quot;-c&quot; 表示允许客户端上传文件</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#676E95;font-style:italic;"># &quot;-l&quot; 表示记录日志，</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#676E95;font-style:italic;"># &quot;-s&quot; 表示将 TFTP_DIRECTORY 设为根目录。</span></span>
<span class="line"><span style="color:#A6ACCD;">TFTP_OPTIONS</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">-c -l -s</span><span style="color:#89DDFF;">&quot;</span></span></code></pre></div><p>重启服务器，让配置生效</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">service</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">tftpd-hpa</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">restart</span></span></code></pre></div><h2 id="测试" tabindex="-1">测试 <a class="header-anchor" href="#测试" aria-label="Permalink to &quot;测试&quot;">​</a></h2><p>上传<img src="`+t+'" alt=""></p><p>下载 <img src="'+p+'" alt=""></p>',14),c=[e];function r(i,y,d,D,C,h){return a(),l("div",null,c)}const T=s(o,[["render",r]]);export{F as __pageData,T as default};
