import{_ as t,o as e,c as a,O as l}from"./chunks/framework.4afe7240.js";const r="/assets/20230606222709497.bbe6b80c.png",s="/assets/20230606224510458.46cd4932.png",P=JSON.parse('{"title":"特殊功能寄存器组织","description":"","frontmatter":{"tags":"概念","title":"特殊功能寄存器组织","date":"2023-06-06T00:00:00.000Z"},"headers":[],"relativePath":"pages/特殊功能寄存器组织.md","filePath":"pages/特殊功能寄存器组织.md","lastUpdated":1686070884000}'),o={name:"pages/特殊功能寄存器组织.md"},i=l('<h1 id="特殊功能寄存器组织" tabindex="-1">特殊功能寄存器组织 <a class="header-anchor" href="#特殊功能寄存器组织" aria-label="Permalink to &quot;特殊功能寄存器组织&quot;">​</a></h1><h2 id="定义" tabindex="-1">定义 <a class="header-anchor" href="#定义" aria-label="Permalink to &quot;定义&quot;">​</a></h2><p>在 <a href="./STM32MP157.html">STM32MP157</a> 中，会将（芯片厂商设计）</p><ul><li><p><a href="./Cortex-A7.html">Cortex-A7</a> 核心的 <a href="./ARMv7特殊功能寄存器.html">特殊功能寄存器</a> （ARM公司设计）</p></li><li><p>以及一些Cortex-A7 处理器没有的特殊功能寄存器</p><ul><li>时钟控制寄存器（Clock Control Register, CCR）</li><li>复位控制寄存器（Reset Control Register, RCR） 重新组织，即<strong>映射到不同的地址或者被重命名</strong>。</li></ul></li><li><p>组织在<strong>Peripherals 1</strong>中的特殊功能寄存器</p><ul><li>包括了一些与外设相关的特殊功能寄存器，例如 GPIO、UART、I2C、SPI 等外设的控制寄存器</li></ul></li><li><p>组织在<strong>Peripherals 2</strong>中的特殊功能寄存器</p><ul><li>包括了 Cortex-A7 处理器的特殊功能寄存器以及一些与系统时钟、复位等相关的特殊功能寄存器</li></ul></li></ul><p><img src="'+r+'" alt=""></p><p>这些寄存器被映射到不同的地址，并且具有不同的名称和用途。</p><p><img src="'+s+'" alt=""></p><h2 id="注意" tabindex="-1">注意 <a class="header-anchor" href="#注意" aria-label="Permalink to &quot;注意&quot;">​</a></h2><ul><li>由于映射的存在，在编写 STM32MP157 的软件时，需要参考 STM32MP157 的文档来了解它们的具体用法和地址映射，而不能直接参考 Cortex-A7 的文档。</li></ul>',9),_=[i];function n(p,c,h,d,u,m){return e(),a("div",null,_)}const f=t(o,[["render",n]]);export{P as __pageData,f as default};
